import { __decorate } from "tslib";
import { Component, Input } from '@angular/core';
import { ArticleService } from "../../services/articles.service";
let ArticlesComponent = class ArticlesComponent {
    constructor(_articleService, router) {
        this._articleService = _articleService;
        this.router = router;
        this.articles = [];
        this.response = [];
    }
    ngOnInit() {
    }
    newTab(url) {
        window.open(url, "_blank");
    }
    removeArticle(data) {
        this._articleService.removeArticle(data._id).subscribe(async (resp) => {
            console.log(resp);
            if (resp.data.removeArticle) {
                this.response = resp.data.removeArticle;
                if (this.response) {
                    this.articles = this.response;
                }
            }
        }, error => {
            console.log(error);
        });
    }
};
__decorate([
