import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { ArticleService } from "../../services/articles.service";
import { Article } from '../../models/article';
import * as AppState from "../../store/app.state";
let ArticlesComponent = class ArticlesComponent {
    constructor(store) {
        this.store = store;
        this.articles$ = store.select(AppState.selectArticles);
        this.response = [];
        this.article = new Article('', '', '');
    }
    ngOnInit() {
    }
    onSubmit() {
        this.getArticles(this.article.topic, this.article.start, this.article.first);
    }
    getArticles(topic, start, first) {
        this._articleService.getArticles(topic, start, first).subscribe(async (resp) => {
            if (resp) {
                this.response = resp;
                if (this.response) {
                    this.articles = this.response;
                }
            }
        }, error => {
            console.log(error);
        });
    }
    newTab(url) {
        window.open(url, "_blank");
    }
};
ArticlesComponent = __decorate([
    Component({
        selector: 'app-articles',
        templateUrl: './articles.component.html',
        styleUrls: ['./articles.component.css'],
        providers: [ArticleService]
    })
], ArticlesComponent);
export { ArticlesComponent };
